import { Modal, ModalHeader, ModalBody } from 'reactstrap';
import { IListState, ISaveMessage, IAPIResult } from '../../GeneralTypes';
import { Popup } from '../../Popup';
import { UserContext } from '../../../context/UserContext';

import RequestDeleteForm from './RequestDeleteForm';
import { IRequestData } from './RequestTypes';
import { RequestAPI } from './RequestAPI';

import { RequestActioncopy } from '../RequestActioncopy';
import { RequestActionclose } from '../RequestActionclose';


class Request extends React.Component<RouteComponentProps<{}>, IListState<IRequestData>> {
    static displayName = Request.name;

    static contextType = UserContext;

    constructor(props: Readonly<RouteComponentProps<{}>>) {
        super(props);
        this.state = {
            data: [],
            loading: false,
            pages: -1,
            currentSort: null,
            currentFilter: null,
            pageSize: 10,
            message: "",
            modalDeleteIsOpen: false,
            currentRecord: undefined,

            // popup stuff
            popupStyle: "",
            popupMessage: "",
            popupErrorList: [],
            popupVisible: false
        };

    }

    private openDeleteModal = (row: { row: { _index: number } }) => {
        this.setState(
            {
                modalDeleteIsOpen: true,
                currentRecord: { ...this.state.data[row.row._index], modifier: "Deleted" }
            });
    }

    private closeDeleteModalNoSave = () => {
        this.setState(
            {
                modalDeleteIsOpen: false,
                currentRecord: undefined
            }
        )
    }

    private closeDeleteModalWithSave = (subaction: string, record: IRequestData) => {
        this.saveOneRecord(subaction, record)
            .then(() =>
            {
                this.loadData({ page: 0, pageSize: this.state.pageSize, sorted: this.state.currentSort, filtered: this.state.currentFilter });
                this.setState(
                    {
                        modalDeleteIsOpen: false,
                        currentRecord: undefined
                    }
                )
            }
            )
            .catch(e => alert(e))
    }

    private saveOneRecord = (subaction: string, record: IRequestData): Promise<IAPIResult<IRequestData>> => {

        const action = (record.modifier === "Added") ? "POST" : (record.modifier === "Deleted") ? "DELETE" : "PUT";
        const toSave: ISaveMessage<IRequestData> = { id: record.id, action: action, dataSubject: { ...record }, subaction: subaction, additionalData: [] };

        // alert("saveOneRecord " + JSON.stringify(toSave));

        return (RequestAPI.saveRecord(toSave, this.context!.access_token)
            .then(result => {
                if (result.success) {
                    this.setState({ ...this.state, currentRecord: result.dataSubject })
                    this.setState({ popupVisible: true, popupMessage: "Save result: " + result.message, popupErrorList: result.additionalInfo, popupStyle: "success" });
                } else {
                    this.setState({ popupVisible: true, popupMessage: "Save failed.", popupErrorList: result.additionalInfo, popupStyle: "danger" });
                }
                return result;
            }
            )
        );
    }

    private loadData = (state: any) => {
        // show the loading overlay
        this.setState({ loading: true })
        // fetch your data

        RequestAPI.loadList({ page: state.page, pageSize: state.pageSize, sorted: state.sorted, filtered: state.filtered }, this.context!.access_token)
            .then(res => {
                // Update react-table
                this.setState({
                    data: res.data,
                    pages: res.pages,
                    currentFilter: state.filtered,
                    currentSort: state.sorted,
                    message: res.message ? res.message : this.state.message
                })
            })
            .catch(e => console.error(e))
            .finally(() => this.setState({ loading: false }))
    }

    downloadToExcel = () => {
        RequestAPI.loadListForExport("Request data", { page: 1, pageSize: this.state.pageSize, sorted: this.state.currentSort, filtered: this.state.currentFilter }, this.context!.access_token);
    }



    //
    // w3-styles are used to have properly behaving dropdown menu in the react-table 
    //
    render() {
        const columns = [
            {
                Header: 'Actions',
                Cell: (row: { row: { id: any, _index: number; }; }) => (
                    <div className="w3-bar">
                        <Link to={"/Requestdetails/" + row.row.id} className="w3-bar-item w3-button" title="Edit">
                            <i className="fa fa-pencil" ></i>
                        </Link>
                        <button onClick={() => { this.openDeleteModal(row); }} className="w3-bar-item w3-button" title="Delete" >
                            <i className="fa fa-trash-o" ></i>
                        </button>
                        <div className="w3-dropdown-hover">
                            <button className="w3-button" title="More actions...">...</button>
                            <div className="w3-dropdown-content w3-bar-block w3-card-4">
                                <a onClick={() => { this.performActioncopy(row); }} className="w3-bar-item w3-button" >Copy</a>
                                <a onClick={() => { this.performActionclose(row); }} className="w3-bar-item w3-button" >Close</a>
                            </div>
                        </div>
                    </div>
                ),
                sortable: false
            },
            {
                Header: 'ID',
   				accessor: 'id',
   				id: 'id',
                show: false
            }
,             {
                Header: 'Code',
   				accessor: 'code',
   				id: 'code',
                show: true
            }
,             {
                Header: 'Title',
   				accessor: 'title',
   				id: 'title',
                show: true
            }
,             {
                Header: 'Description',
   				accessor: 'description',
   				id: 'description',
                show: false
            }
,             {
                Header: 'Start date',
                accessor: (d: IRequestData) => Utils.formatDate(d.startDate),
                // date sorting
                sortMethod: Utils.dateSorter,
				id: 'startDate',
                show: true
            }
,             {
                Header: 'End date',
                accessor: (d: IRequestData) => Utils.formatDate(d.endDate),
                // date sorting
                sortMethod: Utils.dateSorter,
				id: 'endDate',
                show: true
            }
,             {
                Header: 'Status',
   				accessor: 'requestStatusLabel',
   				id: 'requestStatus',
                show: true
            }
,             {
                Header: 'Request type',
   				accessor: 'requestTypeLabel',
   				id: 'requestType',
                show: true
            }
        ];


        return (
            <div className="upromisContent">
                <h5>List of Requests</h5>
                <Link className="w3-button w3-light-grey w3-round" title="Add new record" to="/Requestdetails/add" >
                    <i className="fa fa-plus-circle" ></i>&nbsp;Add new
                </Link>
                <Popup visible={this.state.popupVisible} message={this.state.popupMessage} errorList={this.state.popupErrorList} style={this.state.popupStyle} onDismiss={() => { this.setState({ popupVisible: false }) }} />
                <ReactTable className="-striped"
                    data={this.state.data}
                    pages={this.state.pages}
                    loading={this.state.loading}
                    manual
                    filterable
                    minRows={1}
                    onFetchData={this.loadData}
                    columns={columns}
                />
                <p />
                <button className="w3-button w3-light-grey w3-round" onClick={this.downloadToExcel} title="Export to Excel" >
                    <i className="fa fa-file-excel-o" ></i>&nbsp;Export
                </button>
                <Modal isOpen={this.state.modalDeleteIsOpen} >
                    <ModalHeader toggle={this.closeDeleteModalNoSave} charCode="&times;" >Delete Request</ModalHeader>
                    <ModalBody>
                        <RequestDeleteForm buttonText="Delete" currentData={this.state.currentRecord as IRequestData}  saveAction={this.closeDeleteModalWithSave} />
                    </ModalBody>
                </Modal>
            </div>
        );
    }

    performActioncopy = async (row: { row: { id: any; _index: number; }; }) => {
        // alert("perform Action 1: on " + JSON.stringify(this.state.data[row.row._index]));
        await RequestActioncopy.perform(row.row.id,this.context!.access_token)
        .then( value => {
            // if action is not set to Save, don't Save here
            if(value.action === "Save") {
                this.saveOneRecord(value.subaction, value.dataSubject)
                .then( _ =>
                    this.loadData({ page: 0, pageSize: this.state.pageSize, sorted: this.state.currentSort, filtered: this.state.currentFilter })
                );
            }
            if(value.action === "Navigate")
            {
                console.log("navigate" )
                this.props.history.push(value.subaction, value.dataSubject);
            }
        })
        .catch( ex => 
            this.setState({ popupVisible: true, popupMessage: "Action failed: " + ex.message, popupErrorList: [], popupStyle: "error" })
        );
    }
    
    performActionclose = async (row: { row: { id: any; _index: number; }; }) => {
        // alert("perform Action 1: on " + JSON.stringify(this.state.data[row.row._index]));
        await RequestActionclose.perform(row.row.id,this.context!.access_token)
        .then( value => {
            // if action is not set to Save, don't Save here
            if(value.action === "Save") {
                this.saveOneRecord(value.subaction, value.dataSubject)
                .then( _ =>
                    this.loadData({ page: 0, pageSize: this.state.pageSize, sorted: this.state.currentSort, filtered: this.state.currentFilter })
                );
            }
            if(value.action === "Navigate")
            {
                console.log("navigate" )
                this.props.history.push(value.subaction, value.dataSubject);
            }
        })
        .catch( ex => 
            this.setState({ popupVisible: true, popupMessage: "Action failed: " + ex.message, popupErrorList: [], popupStyle: "error" })
        );
    }
    
}

// required to have lazy loading
export default withRouter(Request);


